import "./App.css";
import { useState } from "react";
import { Route, Routes } from "react-router-dom";
import SearchPage from "./SearchPage";
import Library from "./Library.js"
import {getAll} from "./BooksAPI.js"

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const changeSite = () => {
    !showSearchPage ? setShowSearchpage(true) : setShowSearchpage(false)
  }

const [read, setRead] = useState([]);
const [wantToRead, setWantToRead] = useState([]);
const [reading, setReading] = useState([])


  const loadLibrary = async() => {
    try {
    const allBooks = await getAll();
    console.log(allBooks)
    allBooks.forEach(book => {
      if (book.shelf === "read"){
        setRead([...read,book])
      }
      else if (book.shelf === "wantToRead") {
        setWantToRead([...wantToRead, book])
      }
      else if (book.shelf === "wantToRead"){
        setReading([...reading,book])
      }
      else {
        setWantToRead([...wantToRead, book])
      }
      })
    }
     catch (err){
      console.log(err)
    }
  }

  return (
    <Routes>
    <Route
      exact
      path="/"
      element={
        <Library updateLib={loadLibrary} onChange={changeSite} />
      }
    />
    <Route
      path="/search"
      element={
        <SearchPage updateLib={loadLibrary} onChange={changeSite}
        />
      }
    />
  </Routes>
  )
}

export default App;
