import "./App.css";
import { useState } from "react";
import { Route, Routes } from "react-router-dom";
import SearchPage from "./SearchPage";
import Library from "./Library.js"
import {getAll} from "./BooksAPI.js"

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const changeSite = () => {
    !showSearchPage ? setShowSearchpage(true) : setShowSearchpage(false)
  }

  return (
    <Routes>
    <Route
      exact
      path="/"
      element={
        <Library onChange={changeSite} />
      }
    />
    <Route
      path="/search"
      element={
        <SearchPage onChange={changeSite}
        />
      }
    />
  </Routes>
  )
}

export default App;
